import React from "react";
import { Link } from "react-router-dom";
import "./HomePage.css";

const HomePage = () => {
  const users = [
    { _id: "1", day: "Monday", description: "Make coffee" },
    { _id: "2", day: "Tuesday", description: "Go for a walk" },
    { _id: "3", day: "Wednesday", description: "Read a book" },
    { _id: "4", day: "Thursday", description: "Work on a project" },
    { _id: "5", day: "Friday", description: "Attend a meeting" },
  ];

  return (
    <div className="userlist">
      <h1 className="header">Tasks List</h1>
      <table className="ui celled table">
        <thead>
          <tr>
            <th>Day</th>
            <th>Description</th>
          </tr>
        </thead>
        <tbody>
          {users.map((user) => (
            <tr key={user._id}>
              <td data-label="Day">{user.day}</td>
              <td data-label="Description">{user.description}</td>
            </tr>
          ))}
        </tbody>
      </table>
      <Link to="/createtask">
        <button className="addbtn">Add Task</button>
      </Link>
    </div>
  );
};

export default HomePage;
